import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Col, Row, Container } from '../components/Grid';
// import Jumbotron from '../components/Jumbotron';
import API from '../utils/API';
import { TextArea, FormBtn } from '../components/Form';
// import DeleteBtn from "../components/DeleteBtn";
// import { List, ListItem, } from "../components/List";
// const db = require("../../../models/blog");

class Detail extends Component {
	state = {
		// blogs: [  {response:"" }, { synopsis: '' }, { author: '' }, { topic: '' }],
		response: '',
		blog: [],
	};
	// When this component mounts, grab the book with the _id of this.props.match.params.id
	// e.g. localhost:3000/books/599dcb67f0f16317844583fc
	componentDidMount() {
		this.loadBlog();
	}

	loadBlog = () => {
		API.getBlog(this.props.match.params.id)
			.then(res => {
				console.log('DATA: ', res.data);
				this.setState({
					blog: res.data,

				});
			})
			.catch(err => console.log(err));
	};

	handleResponseChange = event => {
		const { name, value } = event.target;
		this.setState({
			[name]: value,
			response: value,
		});
	};

	Submitresponse = () => {
		console.log(this.state.blog._id);
		if (this.state.blog._id) {
			API.saveBlogId(this.state.blog._id)

				.then(res => {
					console.log(res.data);

					console.log('I ma trying');
				})

				.catch(err => console.log(err));
		}
	};
	Loadresponses = responses => {
		responses.map(x => {
			return console.log(x.response);
		});
	};

	render() {
		const blog = this.state.blog;

		const { topic, author, synopsis } = blog;
		console.log(blog.response);
		return (
			<div>
				<Container fluid>
					<Row>
						<Col size="md-12">
							<h1>
								{topic} by {author}
							</h1>
						</Col>
					</Row>

					<Row>
						<Col size="md-10 md-offset-1">
							<article>
								<h1>Synopsis</h1>
								<h3>{synopsis}</h3>
							</article>
						</Col>
					</Row>
					<form>
						<TextArea
							value={this.state.response}
							onChange={this.handleResponseChange}
							name="response"
							placeholder="Enter your response"
						/>

						<FormBtn disabled={!({ author } && { topic })} onClick={this.Submitresponse}>
							Submit response
						</FormBtn>
					</form>

					<Row>
						<Col size="md-2">
							<Link to="/">← Back to Bloggers</Link>
						</Col>
					</Row>
				</Container>
				<span>
					let {answers} =this.state.blo
				</span>
				{answers.map(x => {
					"yes"
					// return console.log(x.response);
				})}

			</div>
		);
	}
}

export default Detail;
