import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Col, Row, Container } from '../components/Grid';
// import Jumbotron from '../components/Jumbotron';
import API from '../utils/API';
import { TextArea, FormBtn } from '../components/Form';
// import DeleteBtn from "../components/DeleteBtn";
// import { List, ListItem, } from "../components/List";
// const db = require("../../../models/blog");

class Detail extends Component {
	state = {
		responses: '',
		synopsis: '',
		author: '',
		topic: '',
		blog: [],
	};
	// When this component mounts, grab the book with the _id of this.props.match.params.id
	// e.g. localhost:3000/books/599dcb67f0f16317844583fc
	componentDidMount() {
		this.loadBlog();
	}

	loadBlog = () => {
		API.getBlog(this.props.match.params.id)
			.then(res => {
				console.log('DATA: ', res.data);
				console.log('responses: ', res.data.responses);
				this.setState({
					blog: res.data,
				});
			})
			.catch(err => console.log(err));
	};

Loadresponses = () => {



	};

	handleResponseChange = event => {
		const { name, value } = event.target;
		this.setState({
			[name]: value,
			responses: value,
		});
	};

	Submitresponse = event => {
		event.preventDefault();
		console.log('submit!!!!');
		console.log(this.state.blog._id);
		console.log(this.state.responses);
		let responses = this.state.responses;
		const id = this.state.blog._id;
		//   let  responses: [{ response_body: this.state.responses }],
		console.log(id);
		if (this.state.blog._id) {
			API.updateBlogResponse({
				id,
				responses,
			})

				.then(res => {
					console.log(res.data);

					console.log('I ma trying');
				})

				.catch(err => console.log(err));
		}
	};



	render() {
		const userdata = this.state.blog;

		const { topic, author, synopsis } = userdata;
		// const z = userdata;

		// this.Loadresponses(z)

		return (
			<div>
				<Container fluid>
					<Row>
						<Col size="md-12">
							<h1>
								{topic} by {author}
							</h1>
						</Col>
					</Row>

					<Row>
						<Col size="md-10 md-offset-1">
							<article>
								<h1>Synopsis</h1>
								<h3>{synopsis}</h3>
							</article>
						</Col>
					</Row>
					<form>
						<TextArea
							value={this.state.responses}
							onChange={this.handleResponseChange}
							name="response"
							placeholder="Enter your response"
						/>
						<FormBtn onClick={this.Submitresponse}>Submit response</FormBtn>
					</form>

					<Row>
						<Col size="md-2">
							<Link to="/">‚Üê Back to Bloggers</Link>
						</Col>
					</Row>
				</Container>
			</div>
		);
	}
}

export default Detail;
